import React, {Component} from 'react';

class AddFishForm extends Component{
  nameRef = React.createRef();
  priceRef = React.createRef();
  statusRef = React.createRef();
  descRef = React.createRef();
  imageRef = React.createRef();

  createFish = (e) => {
    e.preventDefault();
    console.log(this.nameRef.current.value)
  };

  render() {
    return(
      <form className='fish-edit' onSubmit={this.createFish}>
        <input name='name' refs={this.nameRef} type='text' placeholder='Name'/>
        <input name='price' refs={this.priceRef} type='text' placeholder='Price'/>
        <select name='status' refs={this.statusRef} placeholder='Status'>
          <option value="available">Fresh</option>
          <option value="unavailable">Sold Out</option>
        </select>
        <textarea name='description' refs={this.descRef} placeholder='Description'/>
        <input name='image' refs={this.imageRef} type='text' placeholder='Image'/>
        <button type='submit'>Add Fish</button>
      </form>
    )
  }
}

export default AddFishForm;
